                    Vehicles & Technology
                </li>
                <li className='bodyinfo-option three' onClick={()=>openTabThree}>
                    Offers & Services
                </li>
                <li className='bodyinfo-option four' onClick={()=>openTabFour}>
                    Community
                </li>
            </ul>

            {index===0 && <OptionOne />}
            {index===1 && <OptionTwo/>}
            {index===2 && <OptionThree />}
            {index===3 && <OptionFour />}


        </div>
    )
}

export default BodyInfo;
